package homework2;

public class p39 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d= 2, num1= 5, num2= 4;
		double pi= 3.1415926535, div1= num1/num2, div2= (double)num1/(double)num2;
		
		System.out.println("直徑是"+d+"公分");
		System.out.println("其圓周為"+(d*pi)+"公分");
		System.out.println("5/4等於"+div1);
		System.out.print("5/4等於"+div2);
	}
}